select.execute:
          	var x = y.rest("www.carrosaojose.com.br/veiculos?revenda_id=3639").accept("text/html").get().response;
            var livedata = y.xpath(x, "/html/body/div[3]/div[3]/div[6]/div[2]/div[3]/div[1]/div[2]");
            var output = new XML();

			for each (row in livedata.tr){
				if (row.td[0].*.length() != 0) {
					output += <row>{row.*}</row>;
				}
			}
			response.object = <table>output</table>;
