select.execute:
var urlParaRaspar = "http://www.carrosaojose.com.br/veiculos?revenda_id=3639";
var data = y.rest(urlParaRaspar).accept('text/html').get().response;    

var xpathMapRevenda = {
    rootElement: "//div[contains(@class, 'filtros-lista')][1]",
    foto: "//div[contains(@class, 'photoRev')]/img/@src",
    nome: "//div[contains(@class, 'detRev')][1]//span[contains(@class, 'titleRev')]/text()",
    endereco: "//div[contains(@class, 'endereco')][1]/p//text()",
};

var revendaRootElement = y.xpath(data, xpathMapRevenda.rootElement);

var revenda = {
    id: getId,
    nome: getNome,
    endereco: getEndereco,
    // carros: getCarros,
    // telefones: getTelefones,
}

function getId(){
    return 3639;
}

function getNome(){
    return y.xpath(revendaRootElement, xpathMapRevenda.nome)
}

function getEndereco(){
    return y.xpath(revendaRootElement, xpathMapRevenda.endereco)
}

y.log(y.jsonToXml(revenda));          

response.object = <revenda>
                      <foto>{revenda.foto}</foto>
                      <nome>{revenda.nome}</nome>
                      <endereco>{revenda.endereco}</endereco>
                  </revenda>;
