var resultado = {};
var xpath = "//div[contains(@class, 'produto')]";
var url = "http://www.carrosaojose.com.br/veiculos?revenda_id=3639";
var query   = y.query("SELECT * FROM html WHERE url=@url AND xpath=@xpath", {url: url, xpath: xpath});
resultado.online = true;
var query_xml = y.jsonToXml(query);
resultado.objeto = query_xml;


