select.execute:
var resultado = {};
var xpath = "//div[contains(@class, 'produto')]";
var url = "https://rawgit.com/teles/yql-tables/master/exemplo.html";
var query   = y.query("SELECT * FROM html WHERE url=@url AND xpath=@xpath", {url: url, xpath: xpath});
resultado.online = true;
var ano = y.xpath(query, "//a[contains(@class, 'ano')]");

resultado.ano = ano;

response.object = y.jsonToXml(resultado);
